(240, 0, 1, 'Mereka ditangkap setelah diduga mencuri 6 buah baterai tower base transceiver system (BTS) sebuah provider', 1.62, 0.5, 0.13333333333333, 0, 0.066666666666667, 0.51724137931034, 19),
(241, 1, 2, '\"Kedua pelaku merupakan penjaga tower, namun mereka sendiri yang diduga melakukan pencurian terhadap baterai Tower BTS yang dijaganya,\" urai AKP Dharma Praditya Negara Kasat Reskrim Polres Pinrang, Rabu (9/9/2020)', 1.08, 1, 0.41379310344828, 0.62068965517241, 0.03448275862069, 1, 19),
(242, 2, 3, 'Kedua pelaku kata Dharma diduga bekerja sama untuk mencuri baterai BTS', 1.79, 0.4, 0.27272727272727, 0, 0, 0.37931034482759, 19),
(243, 2, 4, 'Peristiwa itu dilaporkan oleh pihak provider', 0.96, 0, 0.16666666666667, 0, 0, 0.20689655172414, 19),
(244, 3, 5, '\"Mereka dilaporkan oleh pihak provider yang mengalami kerugian sebesar Rp 16,8 juta,\" terangnya', 0.25, 0, 0.15384615384615, 0.41379310344828, 0.076923076923077, 0.44827586206897, 19);
